	 [20.853296932866595, -87.0026272118503],
	 [20.85102715836887, -87.00282317884873]]


var circuito_s =
	[[{lat:20.846878657879266, lng:-86.87786058828249},
	"PTAR Casco Antiguo",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Cuencas.Cuenca_PTAR_Casco_Antiguo.Carcamo_Final_PTAR_Casco_Antiguo",
	1, "ptar"],
	[{lat:20.8597466372908, lng:-86.908323289244},
