	2, "ptar"],
	[{lat:20.87101684233954, lng:-86.89593522751612},
	"PTAR Villas Morelos II",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Cuencas.Cuenca_PTAR_VM2.Carcamo_Final_PTAR_VM2",
	3, "ptar"],
	[{lat:20.8655241587808, lng:-86.90342169610908},
	"PTAR Villas morelos IVl",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Cuencas.Cuenca_PTAR_VM4.Carcamo_Final_PTAR_VM4",
	4, "ptar"],
	[{lat:20.985006553381364, lng:-87.21112678733117},
	"Cárcamo Leona Vicario, Agua Potable",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_LV.Carcamo_Leona_Vicario",
	1, "carcamoAP"],
	[{lat:20.862460343274964, lng:-87.04924381385918},
	"Cárcamo Central Vallarta, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_CV.Carcamo_Central_Vallarta",
	2, "carcamoAP"],
	[{lat:20.859258444692554, lng:-86.90773926330219},
	"Cárcamo Villas Morelos Tanque 1",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Carcamo_Villas_Morelos_Tanque_01",
	3, "carcamoAP"],
	[{lat:20.859478429842095, lng:-86.90764497282822},
