svg.script:
webMI.data.subscribe(webMI.query["base"] + ".Com", function(e) {
var id = "id_2";
var value = e.value;
if (value == 0)
	webMI.gfx.setFill(id, "#ff0000");
if (value == 1)
	webMI.gfx.setFill(id, "#00df00");

});


webMI.data.subscribe(webMI.query["base"] + ".PerdidaDeComunicacion", function(e) {
var id = "id_1";
var value = e.value;
webMI.gfx.setText(id, value);

});


webMI.data.subscribe(webMI.query["base"] + ".Com", function(e) {
var id = "id_3";
var value = e.value;
if (value == 1)
	webMI.gfx.setFill(id, "#00aa00");
if (value == 0)
	webMI.gfx.setFill(id, "#ff0000");

});
