svg.script:


	webMI.data.subscribe(webMI.query["base"] + ".ip", function(e) {
	var id = "id_1";
	var value = e.value;
	webMI.gfx.setText(id, value);

	});

webMI.data.subscribe(webMI.query["base"] + ".short_name", function(e) {
var id = "id_13";
var value = e.value;
webMI.gfx.setText(id, value);

});

	webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
	var id = "id_10";
	var value = e.value;
	if (value == true)
		webMI.gfx.setFill(id, "#fafafa");
	if (value == false)
		webMI.gfx.setFill(id, "#a3a3a3");

	});
	webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
	var id = "id_10";
	var value = e.value;
	if (value == false)
		webMI.gfx.setVisible(id, {0:true,1:false,2:true,3:false,4:true,5:false,6:true,7:false,8:true,9:false,10:true,11:false});
	if (value == true)
		webMI.gfx.setVisible(id, true);

	});






	webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
	var id = "id_17";
	var value = e.status;
	var valor = e.value;
	if (value == 0 && valor == true){
		webMI.gfx.setFill(id, "#000000");}
	else {
		webMI.gfx.setFill(id, "#ffffff");}

	});





	webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
	var id = "id_5";
	var value = e.status;
	var valor = e.value;

	switch (value) {
	case 0:
		if (valor == true)
			webMI.gfx.setFill(id, "#1a8b22");
		else
			webMI.gfx.setFill(id, "#ff0000");
		break;
	case undefined:
		webMI.gfx.setFill(id, "#6b6b6b");
		break;
	case 2156593152:
		webMI.gfx.setFill(id, "#b98f2d");
		break;
	case 2150694912:
		webMI.gfx.setFill(id, "#ff0000");
		break;
	case 2150760448:
		webMI.gfx.setFill(id, "#7c1536");
		break;
	case 1083179008:
		webMI.gfx.setFill(id, "#3a3a3a");
		break;
	default:
		console.log(value);
		webMI.gfx.setFill(id, "#ffffff");
	break;
		}
	});


	webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
	var id = "id_13";

		webMI.gfx.setFill(id, "#ffffff");

	});
