script.code:
/**
 * Code for the layout element button alarm log object display
 * ----------------------------------------
 * This script supports the display showing the alarmlog.
 */


/**
 * DECLARATION SECTION
 */
var accesslist = null;
var anchorX = parseInt(webMI.query["anchorX"]);
var anchorY = parseInt(webMI.query["anchorY"]);
var colorActive = webMI.query["alarm_color"];
var colorInactive = webMI.gfx.getFill("button_access_log_1_outer_frame");
var height = parseInt(webMI.query["height"]);
var isAccessLog = false;
var width = parseInt(webMI.query["width"]);
var x = parseInt(webMI.query["x"]);
var y = parseInt(webMI.query["y"]);
var tableID = webMI.query["tableID"];


/**
 * UI SETUP SECTION
 */
webMI.gfx.setWidth("button_access_log_1_button_bg", webMI.gfx.getWidth("button_access_log_1_button_bg") - 2);
webMI.gfx.setHeight("button_access_log_1_button_bg", webMI.gfx.getHeight("button_access_log_1_button_bg") - 2);
webMI.gfx.setX("button_access_log_1_button_bg", "2");
webMI.gfx.setY("button_access_log_1_button_bg", "2");
webMI.gfx.setWidth("button_access_log_2_button_bg", webMI.gfx.getWidth("button_access_log_2_button_bg") - 2);
webMI.gfx.setHeight("button_access_log_2_button_bg", webMI.gfx.getHeight("button_access_log_2_button_bg") - 2);
webMI.gfx.setX("button_access_log_2_button_bg", "2");
webMI.gfx.setY("button_access_log_2_button_bg", "2");


/**
 * RUNTIME SECTION
 * Runtime code has to be executed after onload to take care all other resources are ready
 */
if (webMI.query["buttonSize"] == "large") {
	webMI.gfx.setVisible("button_access_log_1", false);
} else {
	webMI.gfx.setVisible("button_access_log_2", false);
}

webMI.addOnload(function (e) {
	webMI.addEvent(webMI.data, "permissionnotification", function (notification) {
		setActive(true);
	});
});

/**
 * FUNCTION SECTION
 */
function setActive(activate) {
	if (accesslist == null) {
		webMI.gfx.setFill("button_access_log_1_outer_frame", activate ? {
			0: colorActive,
			2: colorInactive,
			4: colorActive,
			6: colorInactive,
			8: colorActive,
			10: colorInactive
		} : colorInactive);
		webMI.gfx.setFill("button_access_log_2_outer_frame", activate ? {
			0: colorActive,
			2: colorInactive,
			4: colorActive,
			6: colorInactive,
			8: colorActive,
			10: colorInactive
		} : colorInactive);
	} else {
		webMI.gfx.setFill("button_access_log_1_outer_frame", activate ? colorActive : colorInactive);
		webMI.gfx.setFill("button_access_log_2_outer_frame", activate ? colorActive : colorInactive);
	}
	isAccessLog = activate;
}

function handleAccessLog() {
	if (accesslist == null) {
		webMI.gfx.setFill("button_access_log_1_outer_frame", isAccessLog ? colorActive : colorInactive);
		webMI.gfx.setFill("button_access_log_2_outer_frame", isAccessLog ? colorActive : colorInactive);

		webMI.gfx.setFill("context_menu_sign", webMI.query["symbolColorActive"]);

		webMI.gfx.setFill("button_access_log_1_button_bg", webMI.query["fillColorActive"]);
		webMI.gfx.setFill("button_access_log_1_button_label", webMI.query["fontColorActive"]);
		webMI.gfx.setFill("button_access_log_1_button_label_1", webMI.query["fontColorActive"]);
		webMI.gfx.setFill("button_access_log_1_button_label_2", webMI.query["fontColorActive"]);

		webMI.gfx.setFill("button_access_log_2_button_bg", webMI.query["fillColorActive"]);
		webMI.gfx.setFill("button_access_log_2_button_label", webMI.query["fontColorActive"]);
		webMI.gfx.setFill("button_access_log_2_button_label_1", webMI.query["fontColorActive"]);
		webMI.gfx.setFill("button_access_log_2_button_label_2", webMI.query["fontColorActive"]);

		accesslist = webMI.display.openWindow({
			display: webMI.query["accessDisplay"],
			extern: false,
			width: width,
			height: height,
			x: x,
			y: y,
			anchorX: anchorX,
			anchorY: anchorY,
			menubar: false,
			modal: false,
			movable: true,
			resizable: true,
			scrollbars: false,
			status: false,
			title: "T{AccessLog}",
			titlebar: false,
			toolbar: false,
			query: {"tableID": tableID}
		});
		accesslist.cuba = function () {
			accesslist = null;
		}
	} else {
		webMI.gfx.setFill("context_menu_sign", webMI.query["symbolColor"]);

		webMI.gfx.setFill("button_access_log_1_button_bg", webMI.query["fillColor"]);
		webMI.gfx.setFill("button_access_log_1_button_label", webMI.query["fontColor"]);
		webMI.gfx.setFill("button_access_log_1_button_label_1", webMI.query["fontColor"]);
		webMI.gfx.setFill("button_access_log_1_button_label_2", webMI.query["fontColor"]);

		webMI.gfx.setFill("button_access_log_2_button_bg", webMI.query["fillColor"]);
		webMI.gfx.setFill("button_access_log_2_button_label", webMI.query["fontColor"]);
		webMI.gfx.setFill("button_access_log_2_button_label_1", webMI.query["fontColor"]);
		webMI.gfx.setFill("button_access_log_2_button_label_2", webMI.query["fontColor"]);

		accesslist.close();
		accesslist = null;
	}
}


/**
 * TRIGGER SECTION
 */
webMI.trigger.connect("clicked", function (e) {
	handleAccessLog();
	setActive(false);
}, "button_access_log_1");

webMI.trigger.connect("clicked", function (e) {
	handleAccessLog();
	setActive(false);
}, "button_access_log_2");
