script.code:
var data = {};
webMI.trigger.connect("clicked", function(e){
	data.save = 1;
	webMI.display.closeWindow();	
	webMI.trigger.fire(webMI.query.triggerName, data);
}, "save_button");

webMI.trigger.connect("clicked", function(e){
	data.save = 0;
	webMI.display.closeWindow();
	webMI.trigger.fire(webMI.query.triggerName, data);
}, "cancel_button");
