svg.script:
//----------------Inicializo variables -------------------//


//Variable interna
var Info_Windowact=false;
var Markeract=false;
var Markeractdc;
var Markers= new Array();
var Rutas= new Array();
var Pos_origen;
var Pos_destination;
var WayPoints = new Array();

var google = webMI.rootWindow.google; //Clase Principal
var map; //Variable donde va a vivir el mapa
var m,n;
var circuito =
	[[21.07217341145776, -87.030079275378],
	 [21.12180936361552, -86.93144340948847],
	 [21.144807318134383, -86.86654079804543],
	 [21.029158114752377, -86.8521213084531],
	 [21.167737668892737, -86.8324303061414],
	 [21.16837689121474, -86.83002641385961],				
	 [21.17042724096521, -86.84150722591171],
	 [21.174755780337104, -86.85947397937328],
	 [21.175778500181412, -86.8242723491973],
	 [21.17699183295866, -86.81707743940485],
	 [21.180308237536195, -86.81489782760174]]


var circuito_s =
	[[{lat:21.07217341145776, lng:-87.030079275378},
	"Cárcamo 1, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_001.Carcamo_001",
	1, "startpoint"],
	[{lat:21.12180936361552, lng:-86.93144340948847},
	"Cárcamo 2, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_001.Carcamo_002",
	2, "midpoint"],
	[{lat:21.144807318134383, lng:-86.86654079804543},
	"Cárcamo Infonavit 14, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_001.Carcamo_Infonavit14",
	3, "endpoint"],
	[{lat:21.029158114752377, lng:-86.8521213084531},
	"Cárcamo Aeropuerto Zona Urbana, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_011.Carcamo_Aeropuerto_ZU",
	4, "startpoint"],
	[{lat:21.167737668892737, lng:-86.8324303061414},
	"Cárcamo Uxmal, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_001.Carcamo_Uxmal",
	5, "midpoint"],
	[{lat:21.16664566629784, lng:-86.83115264986152},
	"Hiperbólico SM 23, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_001.Carcamo_H23",
	6, "endpoint"],
	[{lat:21.175778500181412, lng:-86.8242723491973},
	"Cárcamo Puerto Juárez, Agua Potable",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Circuitos.Circuito_CAP_008.Carcamo_PJ",
	7, "endpoint"]];

var infowindow;					
var circuito_centro;
const symbolSofrel = "/icons/carcamo_31_40px.png";



//----------------Ejecución -------------------//



//Determinación del dispositivo
var infoCliente;
infoCliente = new webMI.getClientInfo();

//Preprocesamiento de variables

//Calculo del Centro
var suma=[0,0];
for (var i=0; i<circuito.length; i++){
	suma[0]=suma[0]+circuito[i][0]; //Latitud 
	suma[1]=suma[1]+circuito[i][1]; //Longitud
}

circuito_centro=[suma[0]/circuito.length,suma[1]/circuito.length];


webMI.addOnload(function() {

	//Posiciones en el mapa
	var position1 = new google.maps.LatLng(circuito_centro[0], circuito_centro[1]);

	//Opciones del mapa
	var myOptions = {
		zoom: 11.9,
		center: position1,
		mapTypeId: google.maps.MapTypeId.roadmap
	};


	//Inicializo el mapa
	map = new google.maps.Map(document.getElementById("mapa"), myOptions);





	// Instantiate an info window to hold step text.
	const stepDisplay = new google.maps.InfoWindow();


	//-------Inicializo Marcadores

	circuito_s.forEach(([position,title,object,order,type],i) =>{
		const marker = new google.maps.Marker({
			position, 
			map,
			icon: symbolSofrel,
			title: ` ${order}`,
			optimized: false,
		});

		marker.setMap(map);

		//Agrego marcador a lista de marcadores
		Markers.push(marker);

		//-------Listener de Marcadores

		//DOBLECLICK (Fija Carcamo)
		google.maps.event.addListener(marker,'dblclick',function(event) {



			webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Circuito.Eslabon",
			extern:false,height:610,menubar:false,modal:false,movable:true,
			resizable:true,scrollbars:false,status:false,title:title,
			toolbar:false,width:600,x:0,y:50,query:{base:   object}});

			map.panTo(this.getPosition());
			map.setZoom(15);

			Markeract=true;
			Markeractdc=marker;
			Pos_origen=position;


			//Limpio
			Elimina_Ruta(Rutas);

		});
	/*	map.addListener("zoom_changed", () => {
    infowindow.setContent("Zoom: " + map.getZoom());
  });*/


		//CLICK (Abre Infowindow)
		/*google.maps.event.addListener(marker,'click',function(event) {	

			//Abro en temporal
			if (marker!=Markeractdc && Markeract==true ){		
				webMI.display.openDisplay("ObjectTypes.PROJECT.DataLogger"+ type +".Grafico_SectorizacionB", {base:  "AGENT.OBJECTS.Sofrel." + object}, "SectorizacionB");
				Pos_destination=position;	

				//Limpio
				Elimina_Ruta(Rutas);

				//Dibujo
				//Dibuja_Ruta(Pos_origen,Pos_destination);
			}

			//webMI.display.openDisplay("SYSTEM.LIBRARY.PROJECT.OBJECTDISPLAYS.LAYOUTS.Blanc_Layout",{}, "detallesCarcamoNext");

		});	*/


		//MOUSEOVER
		google.maps.event.addListener(marker, "mouseover", function(event){
			infowindow = new google.maps.InfoWindow();
			//Abro marcador
			infowindow.open(marker.getMap,marker);
			Info_Windowact=true;
			//Defino que objeto va a ser el seleccionado para mostrar su información
			webMI.data.subscribe( object +".pressure",
			function(e){
				infowindow.setContent(title+" - Presión: " +
				e.value.toFixed(2) + " kg/cm2");
				}
			);

				//infowindow.setContent(title);

		});	

		//MOUSEOUT
		google.maps.event.addListener(marker, "mouseout", function(event){


			if (Info_Windowact){
				infowindow.close();
				Info_Windowact=false;
			}			
		});



	});	

});



function Dibuja_Ruta(Origen,Destination){

	if(Markeract && (Origen!=Destination)){

		//////////// Servicios Rutas en el mapa
		var directionsService = new google.maps.DirectionsService();
		var directionsRenderer = new google.maps.DirectionsRenderer({ map: map });		

		//Crea el request
		var request = {
			origin: Origen,
			destination:Destination,
			travelMode: google.maps.TravelMode.DRIVING // Puedes ajustar el modo de transporte según tus necesidades
		  };    


		directionsService.route(request, function(response, status) {
			if (status == google.maps.DirectionsStatus.OK) {
			  directionsRenderer.setDirections(response);
			  Rutas.push(directionsRenderer);
			}
		  });
	 }

 }


function Elimina_Ruta(Rutas){

	Rutas.forEach(function(ruta) {
		ruta.setMap(null);
	});

	Rutas=new Array();

 }
