	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_4_Cancun_86_Colonia",
	2, "carcamoAR"],
	[{lat:21.182277177023106, lng:-86.8096049363872},
	"Cárcamo Puertas del Mar, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Puertas_del_Mar",
	3, "carcamoAR"],
	[{lat:21.192133032024543, lng:-86.80655706909526},
	"Cárcamo Playa Blanca, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Playa_Blanca",
	4, "carcamoAR"],
	[{lat:21.18337488197055, lng:-86.81917943245695},
