	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Sureste",
	6, "carcamoAR"],
	[{lat:21.16321775348134, lng:-86.86816206867053},
	"Cárcamo 1 Reg 94, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_1_Reg_94",
	7, "carcamoAR"],
	[{lat:21.166604216682753, lng:-86.8630573852225},
	"Cárcamo 2 Reg 93, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NPE.Carcamo_Guadalupana",
