svg.script:
//----------------Inicializo variables -------------------//


//Variable interna
var Info_Windowact=false;
var Markeract=false;
var Markeractdc;
var Markers= new Array();
var Rutas= new Array();
var Pos_origen;
var Pos_destination;
var WayPoints = new Array();

var google = webMI.rootWindow.google; //Clase Principal
var map; //Variable donde va a vivir el mapa
var m,n;
var circuito =
	[[21.139890022898054, -86.87256515325815],				
	 [21.136529681569726, -86.87231677364156],
	 [21.12647408915108, -86.83107662632888],	
	 [21.10647803293382, -86.84035207689556],	 
	 [21.13926884877736, -86.82331381102499],	 	 
	 [21.141240038027057, -86.84384109696313],	 	 
	 [21.140176456675523, -86.85628629031635],	  	 
	 [21.1376189784048, -86.85666445317105],	 	 
	 [21.138363518564496, -86.85727866943714]]


var circuito_s =
	[[{lat:21.139890022898054, lng:-86.87256515325815},
	"Cárcamo Poniente 18, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_18_Poniente",
	1, "carcamoAR"],
	[{lat:21.136529681569726, lng:-86.87231677364156},
	"Cárcamo 514, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_514",
	2, "carcamoAR"],
	[{lat:21.12647408915108, lng:-86.83107662632888},
	"Cárcamo ST Johns, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_ST_Johns",
	3, "carcamoAR"],
	[{lat:21.10647803293382, lng:-86.84035207689556},
	"Cárcamo Villa Magna, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_LOX.Carcamo_Morelos_I",
	4, "carcamoAR"],
	[{lat:21.13926884877736, lng:-86.82331381102499},
	"Cárcamo Balam, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_Balam",
	5, "carcamoAR"],
	[{lat:21.141240038027057, lng:-86.84384109696313},
	"Cárcamo 2000 Oriente, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_2000_Oriente",
	6, "carcamoAR"],
	[{lat:21.140176456675523, lng:-86.85628629031635},
	"Cárcamo Final 95, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_Final_95",
	7, "carcamoAR"],
	[{lat:21.1376189784048, lng:-86.85666445317105},
	"Cárcamo Sur, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_Final_Sur",
	8, "carcamoAR"],
	[{lat:21.138363518564496, lng:-86.85727866943714},
	"PTAR Caribe 2000, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_Final_Sur",
	9, "ptar"],
	[{lat:21.141323597760575, lng:-86.8253934451595},
	"Cárcamo Poder Judicial, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_CBE.Carcamo_Poder_Judicial",
	10, "carcamoAR"]];

var infowindow;					
var circuito_centro;
var carcamoAR = "/icons/carcamo_ar_30_18px.png";



//----------------Ejecución -------------------//



//Determinación del dispositivo
var infoCliente;
infoCliente = new webMI.getClientInfo();

//Preprocesamiento de variables

//Calculo del Centro
var suma=[0,0];
for (var i=0; i<circuito.length; i++){
	suma[0]=suma[0]+circuito[i][0]; //Latitud 
	suma[1]=suma[1]+circuito[i][1]; //Longitud
}

circuito_centro=[suma[0]/circuito.length,suma[1]/circuito.length];


webMI.addOnload(function() {

	//Posiciones en el mapa
	var position1 = new google.maps.LatLng(circuito_centro[0], circuito_centro[1]);

	//Opciones del mapa
	var myOptions = {
		zoom: 13,
		center: position1,
		mapTypeId: google.maps.MapTypeId.roadmap
	};


	//Inicializo el mapa
	map = new google.maps.Map(document.getElementById("mapa"), myOptions);





	// Instantiate an info window to hold step text.
	const stepDisplay = new google.maps.InfoWindow();


	//-------Inicializo Marcadores

	circuito_s.forEach(([position,title,object,order,type],i) =>{
	if (type == 'ptar') {
	carcamoAR = "/icons/planta.png"
	}
	if (type == 'carcamoAR') {
	carcamoAR = "/icons/carcamo_ar_30_18px.png"
	}

		const marker = new google.maps.Marker({
			position, 
			map,
			icon: carcamoAR,
			title: ` ${order}`,
			optimized: false,
		});

		marker.setMap(map);

		//Agrego marcador a lista de marcadores
		Markers.push(marker);

		//-------Listener de Marcadores

			//DOBLECLICK (Fija Carcamo)
		google.maps.event.addListener(marker,'dblclick',function(event) {



			webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Circuito.Eslabon_AR",
			extern:false,height:610,menubar:false,modal:false,movable:true,
			resizable:true,scrollbars:false,status:false,title:title,
			toolbar:false,width:600,x:0,y:50,query:{base:   object}});

			map.panTo(this.getPosition());
			map.setZoom(15);

			Markeract=true;
			Markeractdc=marker;
			Pos_origen=position;


			//Limpio
			Elimina_Ruta(Rutas);

		});

		//MOUSEOVER
		google.maps.event.addListener(marker, "mouseover", function(event){
			infowindow = new google.maps.InfoWindow();
			//Abro marcador
			infowindow.open(marker.getMap,marker);
			Info_Windowact=true;
			//Defino que objeto va a ser el seleccionado para mostrar su información
			webMI.data.subscribe( object +".level_percent",
			function(e){
				infowindow.setContent(title+" - Nivel: " +
				e.value.toFixed(2) + "%");
				}
			);

				//infowindow.setContent(title);

		});	

		//MOUSEOUT
		google.maps.event.addListener(marker, "mouseout", function(event){


			if (Info_Windowact){
				infowindow.close();
				Info_Windowact=false;
			}			
		});



	});	

});



function Dibuja_Ruta(Origen,Destination){

	if(Markeract && (Origen!=Destination)){

		//////////// Servicios Rutas en el mapa
		var directionsService = new google.maps.DirectionsService();
		var directionsRenderer = new google.maps.DirectionsRenderer({ map: map });		

		//Crea el request
		var request = {
			origin: Origen,
			destination:Destination,
			travelMode: google.maps.TravelMode.DRIVING // Puedes ajustar el modo de transporte según tus necesidades
		  };    


		directionsService.route(request, function(response, status) {
			if (status == google.maps.DirectionsStatus.OK) {
			  directionsRenderer.setDirections(response);
			  Rutas.push(directionsRenderer);
			}
		  });
	 }

 }


function Elimina_Ruta(Rutas){

	Rutas.forEach(function(ruta) {
		ruta.setMap(null);
	});

	Rutas=new Array();

 }
