	 [21.204529348825858, -86.82802707043389],
	 [21.20398488900627, -86.83155188055751]]


var circuito_s =
	[[{lat:21.194445110303494, lng:-86.85873159443128},
	"Cárcamo Villas Otoch, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Villas_Otoch",
