	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Azul_Bonampak",
	3, "carcamoAR"],
	[{lat:21.188364495180362, lng:-86.85072147670647},
	"Cárcamo Infovir 227, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_NTE.Carcamo_Infovir_SM227",
	4, "carcamoAR"],
	[{lat:21.19098939478777, lng:-86.84790584210009},
