//----------------Inicializo variables -------------------//


//Variable interna
var Info_Windowact=false;
var Markeract=false;
var Markeractdc;
var Markers= new Array();
var Rutas= new Array();
var Pos_origen;
var Pos_destination;
var WayPoints = new Array();

var google = webMI.rootWindow.google; //Clase Principal
var map; //Variable donde va a vivir el mapa
var m,n;
var circuito =
	[[21.150604872900104, -86.79457992695514],				
	 [21.144704734246243, -86.78958484404174],
	 [21.138776045454204, -86.76935070332935],	 	
	 [21.134970584626412, -86.75579389075567],	 	 
	 [21.135944852424984, -86.74676379852751],	 	 	 
	 [21.124993881131715, -86.75422867203513],	 	 
	 [21.113007642325226, -86.76116119091363],	 	 	 
	 [21.166604216682753, -86.8630573852225],	 	 	 
	 [21.13054801979613, -86.76234582786012],	 	  
	 [21.132684161789026, -86.76063402103523],	 	 
	 [21.133147135451818, -86.7594821917815]]


var circuito_s =
	[[{lat:21.150604872900104, lng:-86.79457992695514},
	"Cárcamo 1 ZT/CREA, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_1_ZH_CREA",
	1, "carcamoAR"],
	[{lat:21.144704734246243, lng:-86.78958484404174},
	"Cárcamo 2 ZT/Calinda, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_2_ZH_Calinda",
	2, "carcamoAR"],
	[{lat:21.138776045454204, lng:-86.76935070332935},
	"Cárcamo 3 ZT/Tortugas, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_3_ZH_Tortugas",
	3, "carcamoAR"],
	[{lat:21.134970584626412, lng:-86.75579389075567},
	"Cárcamo 4 ZT/Presidente, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_4_ZH_Presidente",
	4, "carcamoAR"],
	[{lat:21.135944852424984, lng:-86.74676379852751},
	"Cárcamo 5 ZT/Convenciones, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_5_ZH_Convenciones",
	5, "carcamoAR"],
	[{lat:21.124993881131715, lng:-86.75422867203513},
	"Cárcamo 6 ZT/Caribe Cancún, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_6_ZH_Caribe_Cancun",
	6, "carcamoAR"],
	[{lat:21.113007642325226, lng:-86.76116119091363},
	"Cárcamo 7 ZT/Pirámides, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_7_ZH_Piramides",
	7, "carcamoAR"],
	[{lat:21.124742568402816, lng:-86.7667975187562},
	"Cárcamo 9 ZT/Pok Ta Pok, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_9_ZH_PokTaPok",
	8, "carcamoAR"],
	[{lat:21.13054801979613, lng:-86.76234582786012},
	"Cárcamo 10 ZT/Pok Ta Pok, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_10_ZH_PokTaPok",
	9, "carcamoAR"],
	[{lat:21.132684161789026, lng:-86.76063402103523},
	"Cárcamo 11 ZT/Pok Ta Pok, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_11_ZH_PokTaPok",
	10, "carcamoAR"],
	[{lat:21.133147135451818, lng:-86.7594821917815},
	"PTAR Pok Ta Pok, Agua Residual",
	"AGENT.OBJECTS.SCADA.Benito_Juarez.Cuencas.Cuenca_CAR_PTP.Carcamo_11_ZH_PokTaPok",
	11, "ptar"]];

var infowindow;					
var circuito_centro;
var carcamoAR = "/icons/carcamo_ar_30_18px.png";



//----------------Ejecución -------------------//



//Determinación del dispositivo
var infoCliente;
infoCliente = new webMI.getClientInfo();

//Preprocesamiento de variables

//Calculo del Centro
var suma=[0,0];
for (var i=0; i<circuito.length; i++){
	suma[0]=suma[0]+circuito[i][0]; //Latitud 
	suma[1]=suma[1]+circuito[i][1]; //Longitud
}

circuito_centro=[suma[0]/circuito.length,suma[1]/circuito.length];


webMI.addOnload(function() {

	//Posiciones en el mapa
	var position1 = new google.maps.LatLng(circuito_centro[0], circuito_centro[1]);

	//Opciones del mapa
	var myOptions = {
		zoom: 13,
		center: position1,
		mapTypeId: google.maps.MapTypeId.roadmap
	};


	//Inicializo el mapa
	map = new google.maps.Map(document.getElementById("mapa"), myOptions);





	// Instantiate an info window to hold step text.
	const stepDisplay = new google.maps.InfoWindow();


	//-------Inicializo Marcadores

	circuito_s.forEach(([position,title,object,order,type],i) =>{
	if (type == 'ptar') {
	carcamoAR = "/icons/planta.png"
	}


		const marker = new google.maps.Marker({
			position, 
			map,
			icon: carcamoAR,
			title: ` ${order}`,
			optimized: false,
		});

		marker.setMap(map);

		//Agrego marcador a lista de marcadores
		Markers.push(marker);

		//-------Listener de Marcadores

		//DOBLECLICK (Fija Carcamo)
		google.maps.event.addListener(marker,'dblclick',function(event) {



			webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Circuito.Eslabon_AR",
			extern:false,height:610,menubar:false,modal:false,movable:true,
			resizable:true,scrollbars:false,status:false,title:title,
			toolbar:false,width:600,x:0,y:50,query:{base:   object}});

			map.panTo(this.getPosition());
			map.setZoom(15);

			Markeract=true;
			Markeractdc=marker;
			Pos_origen=position;


			//Limpio
			Elimina_Ruta(Rutas);

		});
	/*	map.addListener("zoom_changed", () => {
    infowindow.setContent("Zoom: " + map.getZoom());
  });*/



		//MOUSEOVER
		google.maps.event.addListener(marker, "mouseover", function(event){
			infowindow = new google.maps.InfoWindow();
			//Abro marcador
			infowindow.open(marker.getMap,marker);
			Info_Windowact=true;
			//Defino que objeto va a ser el seleccionado para mostrar su información
			webMI.data.subscribe( object +".level_percent",
			function(e){
				infowindow.setContent(title+" - Nivel: " +
				e.value.toFixed(2) + "%");
				}
			);

				//infowindow.setContent(title);

		});	

		//MOUSEOUT
		google.maps.event.addListener(marker, "mouseout", function(event){


			if (Info_Windowact){
				infowindow.close();
				Info_Windowact=false;
			}			
		});



	});	

});



function Dibuja_Ruta(Origen,Destination){

	if(Markeract && (Origen!=Destination)){

		//////////// Servicios Rutas en el mapa
		var directionsService = new google.maps.DirectionsService();
		var directionsRenderer = new google.maps.DirectionsRenderer({ map: map });		

		//Crea el request
		var request = {
			origin: Origen,
			destination:Destination,
			travelMode: google.maps.TravelMode.DRIVING // Puedes ajustar el modo de transporte según tus necesidades
		  };    


		directionsService.route(request, function(response, status) {
			if (status == google.maps.DirectionsStatus.OK) {
			  directionsRenderer.setDirections(response);
			  Rutas.push(directionsRenderer);
			}
		  });
	 }

 }


function Elimina_Ruta(Rutas){

	Rutas.forEach(function(ruta) {
		ruta.setMap(null);
	});

	Rutas=new Array();

 }
