//----------------Inicializo variables -------------------//

//Variable interna
var Info_Windowact=false;
var Markeract=false;
var Markeractdc;
var Markers= new Array();
var Rutas= new Array();
var Pos_origen;
var Pos_destination;
var WayPoints = new Array();

var google = webMI.rootWindow.google; //Clase Principal
var map; //Variable donde va a vivir el mapa
var m,n;
var circuito =
	[[20.985006553381364, -87.21112678733117],
	 [20.859258444692554, -86.90773926330219],
	 [20.859478429842095, -86.90764497282822],
	 [20.980814886617704, -87.22764376634494],
	 [20.975269209887443, -87.23808211459635],
	 [20.858473945827868, -87.00222187666945],
	 [20.855824071291995, -87.00244007602647],
	 [20.853296932866595, -87.0026272118503],
	 [20.85102715836887, -87.00282317884873],
	 [20.862460343274964, -87.04924381385918]]


var circuito_s =
	[[{lat:20.985006553381364, lng:-87.21112678733117},
	"Cárcamo Leona Vicario, Agua Potable",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_LV.Carcamo_Leona_Vicario",
	1, "carcamoAP"],
	[{lat:20.862460343274964, lng:-87.04924381385918},
	"Cárcamo Central Vallarta, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_CV.Carcamo_Central_Vallarta",
	2, "carcamoAP"],
	[{lat:20.859258444692554, lng:-86.90773926330219},
	"Cárcamo Villas Morelos Tanque 1",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Carcamo_Villas_Morelos_Tanque_01",
	3, "carcamoAP"],
	[{lat:20.859478429842095, lng:-86.90764497282822},
	"Cárcamo Villas Morelos Tanque 2",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Carcamo_Villas_Morelos_Tanque_02",
	4 , "carcamoAP"],
	[{lat:20.980814886617704, lng:-87.22764376634494},
	"Pozo Nuevo, Leona Vicario",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_LV.Pozo_Nuevo_LV",
	5, "pozo"],
	[{lat:20.975269209887443, lng:-87.23808211459635},
	"Pozo Viejo, Leona Vicario",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_LV.Pozo_Viejo_LV",
	6, "pozo"],
	[{lat:20.858473945827868, lng:-87.00222187666945},
	"Pozo 1, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Pozo_001_PM",
	7, "pozo"],
	[{lat:20.855824071291995, lng:-87.00244007602647},
	"Pozo 2, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Pozo_002_PM",
	8 , "pozo"],
	[{lat:20.853296932866595, lng:-87.0026272118503},
	"Pozo 3, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Pozo_003_PM",
	9, "pozo"],
	[{lat:20.85102715836887, lng:-87.00282317884873},
	"Pozo 4, Puerto Morelos",
	"AGENT.OBJECTS.SCADA.Puerto_Morelos.Circuitos.Circuito_PM.Pozo_004_PM",
	10, "pozo"]];

var infowindow;					
var circuito_centro;
var carcamoAP = "/icons/carcamo_31_40px.png";



//----------------Ejecución -------------------//



//Determinación del dispositivo
var infoCliente;
infoCliente = new webMI.getClientInfo();

//Preprocesamiento de variables

//Calculo del Centro
var suma=[0,0];
for (var i=0; i<circuito.length; i++){
	suma[0]=suma[0]+circuito[i][0]; //Latitud 
	suma[1]=suma[1]+circuito[i][1]; //Longitud
}

circuito_centro=[suma[0]/circuito.length,suma[1]/circuito.length];


webMI.addOnload(function() {

	//Posiciones en el mapa
	var position1 = new google.maps.LatLng(circuito_centro[0], circuito_centro[1]);

	//Opciones del mapa
	var myOptions = {
		zoom: 11,
		center: position1,
		mapTypeId: google.maps.MapTypeId.roadmap
	};


	//Inicializo el mapa
	map = new google.maps.Map(document.getElementById("mapa"), myOptions);





	// Instantiate an info window to hold step text.
	const stepDisplay = new google.maps.InfoWindow();


	//-------Inicializo Marcadores

	circuito_s.forEach(([position,title,object,order,type],i) =>{
		if (type == 'pozo') {
			carcamoAP = "/icons/image.png"
		}
		if (type == 'carcamoAP') {
			carcamoAP = "/icons/carcamo_31_40px.png"
		}		

		const marker = new google.maps.Marker({
			position, 
			map,
			icon: carcamoAP,
			title: ` ${order}`,
			optimized: false,
		});

		marker.setMap(map);

		//Agrego marcador a lista de marcadores
		Markers.push(marker);

		//-------Listener de Marcadores

		//DOBLECLICK (Fija Carcamo)

		//DOBLECLICK (Fija Carcamo)
		google.maps.event.addListener(marker,'dblclick',function(event) {



			if (type == 'carcamoAP') {
				webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Circuito.Eslabon",
			extern:false,height:610,menubar:false,modal:false,movable:true,
			resizable:true,scrollbars:false,status:false,title:title,
			toolbar:false,width:600,x:0,y:50,query:{base:   object}});
					}
			if (type == 'pozo') {
			console.log(type + " " + object);
				webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Circuito.Eslabon_Pozo",
			extern:false,height:610,menubar:false,modal:false,movable:true,
			resizable:true,scrollbars:false,status:false,title:title,
			toolbar:false,width:600,x:0,y:50,query:{base:   object}});
					}

			map.panTo(this.getPosition());
			map.setZoom(15);

			Markeract=true;
			Markeractdc=marker;
			Pos_origen=position;


			//Limpio
			Elimina_Ruta(Rutas);

		});



		//MOUSEOVER
		google.maps.event.addListener(marker, "mouseover", function(event){
			infowindow = new google.maps.InfoWindow();
			//Abro marcador
			infowindow.open(marker.getMap,marker);
			Info_Windowact=true;
			//Defino que objeto va a ser el seleccionado para mostrar su información
			webMI.data.subscribe( object +".pressure",
			function(e){
				infowindow.setContent(title+" - Presión: " +
				e.value.toFixed(2) + "kg/cm2");
				}
			);

				//infowindow.setContent(title);

		});	

		//MOUSEOUT
		google.maps.event.addListener(marker, "mouseout", function(event){


			if (Info_Windowact){
				infowindow.close();
				Info_Windowact=false;
			}			
		});



	});	

});



function Dibuja_Ruta(Origen,Destination){

	if(Markeract && (Origen!=Destination)){

		//////////// Servicios Rutas en el mapa
		var directionsService = new google.maps.DirectionsService();
		var directionsRenderer = new google.maps.DirectionsRenderer({ map: map });		

		//Crea el request
		var request = {
			origin: Origen,
			destination:Destination,
			travelMode: google.maps.TravelMode.DRIVING // Puedes ajustar el modo de transporte según tus necesidades
		  };    


		directionsService.route(request, function(response, status) {
			if (status == google.maps.DirectionsStatus.OK) {
			  directionsRenderer.setDirections(response);
			  Rutas.push(directionsRenderer);
			}
		  });
	 }

 }


function Elimina_Ruta(Rutas){

	Rutas.forEach(function(ruta) {
		ruta.setMap(null);
	});

	Rutas=new Array();

 }
