svg.script:
webMI.data.subscribe(webMI.query["base"] + ".freq", function(e) {
var id = "id_6";
var value = e.value;
var status = e.status;
if (status !== 0)
webMI.gfx.setText(id, " ");
else
webMI.gfx.setText(id, webMI.sprintf("%.1f", value));

});
webMI.data.subscribe(webMI.query["base"] + ".man_auto", function(e) {
var id = "id_7";
var value = e.value;
if (value == false)
	webMI.gfx.setText(id, "T{MAN}");
if (value == true)
	webMI.gfx.setText(id, "T{AUTO}");

});
webMI.data.subscribe(webMI.query["base"] + ".loc_rem", function(e) {
var id = "id_3";
var value = e.value;
if (value == false)
	webMI.gfx.setText(id, "T{LOC}");
if (value == true)
	webMI.gfx.setText(id, "T{REM}");

});

webMI.data.subscribe(webMI.query["base"] + ".status", function(e) {
var id = "id_1";
var value = e.value;

if (value == false){

	webMI.data.subscribe(webMI.query["base"] + ".current", function(e) {
	var id = "id_1";
	var value = e.value;
	if (0 <= value && value < 1)
		webMI.gfx.setFill(id, "#929292");
	if (1.1 <= value && value < 100)
		webMI.gfx.setFill(id, "#61d836");
	});
	}

if (value == true)
	webMI.gfx.setFill(id, "#61d836");

});


webMI.data.subscribe(webMI.query["base"] + ".fault", function(e) {
var id = "id_5";
var value = e.value;
if (value == true)
	webMI.gfx.setStroke(id, "#ee220c");
if (value == false)
	webMI.gfx.setStroke(id, "#000000");

});
webMI.data.subscribe(webMI.query["base"] + ".fault", function(e) {
var id = "id_5";
var value = e.value;
if (value == true)
	webMI.gfx.setVisible(id, {0:true,2:false,4:true,6:false,8:true,10:false});
if (value == false)
	webMI.gfx.setVisible(id, false);

});




webMI.data.subscribe(webMI.query["base"] + ".freq", function(e) {
var id = "id_1";
var value = e.value;
var status = e.status;
if (0 <= value && value < 1 || status == undefined)
	webMI.gfx.setFill(id, "#929292");
if (1.1 <= value && value < 100)
	webMI.gfx.setFill(id, "#61d836");

});

webMI.addEvent("id_4", "click", function(e) {
var id = "id_4";
var value = true;
webMI.display.openWindow({display:"SYSTEM.DISPLAYS.Equipos.equipo",extern:false,height:800,menubar:false,modal:false,movable:true,resizable:true,scrollbars:false,status:false,title:"",toolbar:false,width:1000,query:webMI.query});
});

webMI.data.subscribe(webMI.query["base"] + ".freq", function(e) {
var id = "id_2";
var value = e.value;
var status = e.status;
console.log(value);
if (status == undefined)
	webMI.gfx.setVisible(id, false);
if (status == "true")
	webMI.gfx.setVisible(id, true);

});


webMI.data.subscribe(webMI.query["base"] + ".name", function(e) {
var id = "id_8";
var value = e.value;
webMI.gfx.setText(id, value);

});
