	webMI.gfx.setText(id, "T{Falla}");
if (value == false) {
	webMI.data.subscribe(webMI.query["base"] + ".name", function(e) {
	var valor = e.value;
	webMI.gfx.setText(id, valor);
	});
}

});



webMI.data.subscribe(webMI.query["base"] + ".name", function(e) {
var id = "id_1";
var value = e.value;
console.log(typeof value + " " + webMI.query["base"]);
if (typeof value == 'undefined')
	webMI.gfx.setVisible(id, false);
if (typeof value !== 'undefined')
	webMI.gfx.setVisible(id, true);

});


webMI.addEvent("id_1", "click", function(e) {
