svg.script:
webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
var id = "id_10";
var value = e.value;
if (value == true)
	webMI.gfx.setFill(id, "#fafafa");
if (value == false)
	webMI.gfx.setFill(id, "#a3a3a3");

});
webMI.data.subscribe(webMI.query["base"] + ".connectivity", function(e) {
var id = "id_10";
var value = e.value;
if (value == false)
	webMI.gfx.setVisible(id, {0:true,1:false,2:true,3:false,4:true,5:false,6:true,7:false,8:true,9:false,10:true,11:false});
if (value == true)
	webMI.gfx.setVisible(id, true);

});

webMI.data.subscribe(webMI.query["base"] + ".sap_name", function(e) {
var id = "id_13";
var value = e.value;
webMI.gfx.setText(id, value);

});



